import Colaborador from "../Colaborador";
import "./Time.css";
import { useState } from "react";

const Time = (props) => {
  const [cor, setCor] = useState(props.corPrimaria);
  return (
    props.colaboradores.length > 0 && (
      <section
        className="time"
        style={{ backgroundColor: props.corSecundaria, backgroundImage: 'url(/imagens/fundo.png)' }}
      >
        <input type="color" className="input-cor" onChange={(evento) => setCor(evento.target.value)} value={cor} />
        <h3 style={{ borderColor: props.corPrimaria }}>{props.nome}</h3>

        <div className="colaboradores">
          {props.colaboradores.map((colaborador) => {
            return (
              <Colaborador
                corDeFundo={props.corPrimaria}
                key={colaborador.nome}
                nome={colaborador.nome}
                cargo={colaborador.cargo}
                imagem={colaborador.imagem}
                aoDeletar={props.aoDeletar}
              />
            );
          })}
        </div>
      </section>
    )
  );
};

export default Time;
